sql.where:





            AND fm.fund_name LIKE '%' || #{pageRequestDTO.keyword} || '%'



            AND fm.fund_code = #{pageRequestDTO.keyword}



            AND fm.fund_type LIKE '%' || #{pageRequestDTO.keyword} || '%'






    AND fm.fund_type = #{pageRequestDTO.category}




    AND fm.oper_status = #{pageRequestDTO.status}


AND fm.oper_status = '운용중' or fm.oper_status = '운용중단' or fm.oper_status = '운용대기'

mapper.select:
SELECT /* ------------------------- * 1) 요약 정보 * ------------------------- */ fm.fund_code AS fundCode, fm.fund_name AS fundName, fdh.nav_per_unit AS navPerUnit, fdh.daily_change_rate AS dailyChangeRate, fdh.total_nav AS totalNav, fdh.setup_origin_amount AS setupOriginAmount, TO_CHAR(fdh.trade_date, 'YYYY-MM-DD') AS tradeDate,
/* ------------------------- * 2) FUND_MASTER 기본 정보 * ------------------------- */ fc.category_name AS categoryName, fm.fund_type AS fundType, fm.invest_region AS investRegion, fm.classify_code AS classifyCode, TO_CHAR(fm.setup_date, 'YYYY-MM-DD') AS setupDate, fm.initial_nav AS initialNav, fm.public_private_type AS publicPrivateType, fm.oper_status AS operStatus, fm.fund_short_code AS shortCode, fm.is_unit_type AS isUnitType, fm.oper_period_type AS operPeriodType, fm.class_name AS className,
fm.rgn_type AS rgnType, fm.prfm_type AS prfmType,
/* ------------------------- * 3) FUND_FEE_STRUCTURE 보수/수수료 * ------------------------- */ ffs.sales_fee_rate AS salesFeeRate, ffs.mgt_fee_rate AS mgtFeeRate, ffs.trustee_fee_rate AS trusteeFeeRate, ffs.admin_fee_rate AS adminFeeRate, ffs.total_expense_ratio AS totalExpenseRatio, ffs.total_fee_rate AS totalFeeRate,
ffs.upfront_fee AS upfrontFee, ffs.post_fee_rate AS postFeeRate,
/* ------------------------- * 4) 관련회사 정보 * ------------------------- */ amc.operator_name AS operatorName, fm.admin_company AS adminCompany, fm.sales_company AS salesCompany, fm.trustee_company AS trusteeCompany,
/* ------------------------- * 5) 회사개황/로고/재무현황 * ------------------------- */ amc.operator_hp AS operatorHp, amc.ceo_name AS ceoName, TO_CHAR(amc.establishment_date, 'YYYY-MM-DD') AS establishmentDate, amc.total_asset AS totalAsset, amc.total_liabilities AS totalLiabilities, amc.capital AS capital, amc.equity_capital AS equityCapital, amc.homepage_url AS homepageUrl, amc.logo AS logo, amc.net_income AS netIncome, amc.last_modified_date AS lastModifiedDate,
/* 계산 컬럼 - 영업용순자본 */ (amc.total_asset - amc.total_liabilities) AS operatingCapital,
/* 전일 NAV */ ( SELECT nav_per_unit FROM fund_daily_history WHERE fund_code = fm.fund_code AND trade_date < fdh.trade_date ORDER BY trade_date DESC FETCH FIRST 1 ROW ONLY ) AS prevDayNav,
/* 전주 NAV */ ( SELECT nav_per_unit FROM fund_daily_history WHERE fund_code = fm.fund_code AND trade_date <= fdh.trade_date - 7 ORDER BY trade_date DESC FETCH FIRST 1 ROW ONLY ) AS prevWeekNav,
/* 전주대비 금액 */ ( fdh.nav_per_unit - ( SELECT nav_per_unit FROM fund_daily_history WHERE fund_code = fm.fund_code AND trade_date <= fdh.trade_date - 7 ORDER BY trade_date DESC FETCH FIRST 1 ROWS ONLY ) ) AS prevWeekChange,
/* 전주대비 퍼센트 */ ( CASE WHEN ( SELECT nav_per_unit FROM fund_daily_history WHERE fund_code = fm.fund_code AND trade_date <= fdh.trade_date - 7 ORDER BY trade_date DESC FETCH FIRST 1 ROWS ONLY ) IS NULL OR ( SELECT nav_per_unit FROM fund_daily_history WHERE fund_code = fm.fund_code AND trade_date <= fdh.trade_date - 7 ORDER BY trade_date DESC FETCH FIRST 1 ROWS ONLY ) = 0 THEN NULL ELSE ( (fdh.nav_per_unit - (SELECT nav_per_unit FROM fund_daily_history WHERE fund_code = fm.fund_code AND trade_date <= fdh.trade_date - 7 ORDER BY trade_date DESC FETCH FIRST 1 ROWS ONLY ) ) / (SELECT nav_per_unit FROM fund_daily_history WHERE fund_code = fm.fund_code AND trade_date <= fdh.trade_date - 7 ORDER BY trade_date DESC FETCH FIRST 1 ROWS ONLY ) ) * 100 END ) AS prevWeekPercent
FROM fund_master fm
/* 최신 기준가 */ LEFT JOIN fund_daily_history fdh ON fdh.fund_code = fm.fund_code AND fdh.trade_date = ( SELECT MAX(f2.trade_date) FROM fund_daily_history f2 WHERE f2.fund_code = fm.fund_code )
/* 보수/수수료 */ LEFT JOIN fund_fee_structure ffs ON fm.fund_code = ffs.fund_code
/* 운용사 정보 */ LEFT JOIN asset_management_company amc ON fm.asset_manager_id = amc.operator_id /* 펀드 유형 - 카테고리 한글이름 */ LEFT JOIN FUND_CATEGORY fc ON fm.fund_type = fc.category_code
WHERE fm.fund_code = #{fundCode}

SELECT * FROM ( SELECT TO_CHAR(trade_date, 'YYYY-MM-DD') AS tradeDate, setup_origin_amount AS setupOriginAmount,
/* NAV 수익률 */ CASE WHEN ROW_NUMBER() OVER (ORDER BY trade_date ASC) = 1 THEN 0 WHEN LAG(nav_per_unit) OVER (ORDER BY trade_date ASC) IS NULL THEN 0 WHEN LAG(nav_per_unit) OVER (ORDER BY trade_date ASC) = 0 THEN 0 ELSE ROUND( (nav_per_unit / LAG(nav_per_unit) OVER (ORDER BY trade_date ASC) - 1) * 100, 2 ) END AS navReturn,
/* KOSPI 수익률 */ CASE WHEN ROW_NUMBER() OVER (ORDER BY trade_date ASC) = 1 THEN 0 WHEN LAG(bm_kospi) OVER (ORDER BY trade_date ASC) IS NULL THEN 0 WHEN LAG(bm_kospi) OVER (ORDER BY trade_date ASC) = 0 THEN 0 ELSE ROUND( (bm_kospi / LAG(bm_kospi) OVER (ORDER BY trade_date ASC) - 1) * 100, 2 ) END AS kospiReturn,
/* KOSDAQ 수익률 */ CASE WHEN ROW_NUMBER() OVER (ORDER BY trade_date ASC) = 1 THEN 0 WHEN LAG(bm_kosdaq) OVER (ORDER BY trade_date ASC) IS NULL THEN 0 WHEN LAG(bm_kosdaq) OVER (ORDER BY trade_date ASC) = 0 THEN 0 ELSE ROUND( (bm_kosdaq / LAG(bm_kosdaq) OVER (ORDER BY trade_date ASC) - 1) * 100, 2 ) END AS kosdaqReturn,
/* 국공채 수익률 */ CASE WHEN ROW_NUMBER() OVER (ORDER BY trade_date ASC) = 1 THEN 0 WHEN LAG(bm_gov_bond) OVER (ORDER BY trade_date ASC) IS NULL THEN 0 WHEN LAG(bm_gov_bond) OVER (ORDER BY trade_date ASC) = 0 THEN 0 ELSE ROUND( (bm_gov_bond / LAG(bm_gov_bond) OVER (ORDER BY trade_date ASC) - 1) * 100, 2 ) END AS govBondReturn,
/* 회사채 수익률 */ CASE WHEN ROW_NUMBER() OVER (ORDER BY trade_date ASC) = 1 THEN 0 WHEN LAG(bm_corp_bond) OVER (ORDER BY trade_date ASC) IS NULL THEN 0 WHEN LAG(bm_corp_bond) OVER (ORDER BY trade_date ASC) = 0 THEN 0 ELSE ROUND( (bm_corp_bond / LAG(bm_corp_bond) OVER (ORDER BY trade_date ASC) - 1) * 100, 2 ) END AS corpBondReturn
FROM fund_daily_history WHERE fund_code = #{fundCode} ORDER BY trade_date ASC ) ORDER BY tradeDate ASC
